/**
 * Enhanced IndexedDB Manager with query capabilities
 */
 
 // Assuming 'instance' is your existing object
// If it doesn't exist, uncomment the following line
// const instance = {};

// Database configuration
const DB_NAME = "digitalForms";

class IndexedDBManager {
  constructor(db) {
    this.db = db;
  }

  /**
   * Delete entries from a store that match a condition
   * @param {string} storeName - The name of the store
   * @param {Object} whereCondition - Condition to match entries (e.g., { form_type: 'VI', leased: true })
   * @returns {Promise} - Resolves with the count of deleted items
   */
  deleteEntryWhere(storeName, whereCondition) {
    return new Promise((resolve, reject) => {
      try {
        const transaction = this.db.transaction([storeName], "readwrite");
        const store = transaction.objectStore(storeName);
        let deletedCount = 0;
        let matchingKeys = [];

        // First, find all matching records
        const getAllRequest = store.openCursor();
        
        getAllRequest.onsuccess = (event) => {
          const cursor = event.target.result;
          
          if (cursor) {
            const record = cursor.value;
            
            // Check if record matches the where condition
            if (this._matchesCondition(record, whereCondition)) {
              matchingKeys.push(cursor.primaryKey);
            }
            
            cursor.continue();
          } else {
            // No more records, now delete all matching records
            console.log(`Found ${matchingKeys.length} records to delete in ${storeName}`);
            
            if (matchingKeys.length === 0) {
              resolve(0);
              return;
            }
            
            // Delete each matching record
            const deletePromises = matchingKeys.map(key => {
              return new Promise((deleteResolve, deleteReject) => {
                const deleteRequest = store.delete(key);
                
                deleteRequest.onsuccess = () => {
                  deletedCount++;
                  deleteResolve();
                };
                
                deleteRequest.onerror = (evt) => {
                  console.error(`Error deleting key ${key} from ${storeName}:`, evt.target.error);
                  deleteReject(evt.target.error);
                };
              });
            });
            
            Promise.all(deletePromises)
              .then(() => {
                console.log(`Successfully deleted ${deletedCount} entries from ${storeName}`);
                resolve(deletedCount);
              })
              .catch(error => {
                reject(error);
              });
          }
        };
        
        getAllRequest.onerror = (event) => {
          console.error(`Error finding entries in ${storeName}:`, event.target.error);
          reject(event.target.error);
        };
        
        transaction.onerror = (event) => {
          console.error(`Transaction error in ${storeName}:`, event.target.error);
          reject(event.target.error);
        };
      } catch (error) {
        console.error("Error in deleteEntryWhere:", error);
        reject(error);
      }
    });
  }

  /**
   * Update entries in a store that match a condition
   * @param {string} storeName - The name of the store
   * @param {Object} whereCondition - Condition to match entries (e.g., { form_type: 'VI' })
   * @param {Object} updateData - Data to update (will be merged with existing data)
   * @returns {Promise} - Resolves with the count of updated items
   */
  updateEntryWhere(storeName, whereCondition, updateData) {
    return new Promise((resolve, reject) => {
      try {
        const transaction = this.db.transaction([storeName], "readwrite");
        const store = transaction.objectStore(storeName);
        let updatedCount = 0;
        
        // Use a cursor to iterate through all records
        const cursorRequest = store.openCursor();
        
        cursorRequest.onsuccess = (event) => {
          const cursor = event.target.result;
          
          if (cursor) {
            const record = cursor.value;
            
            // Check if record matches the where condition
            if (this._matchesCondition(record, whereCondition)) {
              // Merge the existing record with the update data
              const updatedRecord = { ...record, ...updateData };
              
              // Update the record
              const updateRequest = cursor.update(updatedRecord);
              
              updateRequest.onsuccess = () => {
                updatedCount++;
              };
              
              updateRequest.onerror = (evt) => {
                console.error(`Error updating record in ${storeName}:`, evt.target.error);
              };
            }
            
            // Move to the next record
            cursor.continue();
          } else {
            // No more records
            console.log(`Updated ${updatedCount} entries in ${storeName}`);
            resolve(updatedCount);
          }
        };
        
        cursorRequest.onerror = (event) => {
          console.error(`Error finding entries in ${storeName}:`, event.target.error);
          reject(event.target.error);
        };
        
        transaction.onerror = (event) => {
          console.error(`Transaction error in ${storeName}:`, event.target.error);
          reject(event.target.error);
        };
      } catch (error) {
        console.error("Error in updateEntryWhere:", error);
        reject(error);
      }
    });
  }

  /**
   * Get entries from a store that match a condition
   * @param {string} storeName - The name of the store
   * @param {Object} whereCondition - Condition to match entries
   * @returns {Promise} - Resolves with an array of matching entries
   */
  getEntriesWhere(storeName, whereCondition) {
    return new Promise((resolve, reject) => {
      try {
        const transaction = this.db.transaction([storeName], "readonly");
        const store = transaction.objectStore(storeName);
        const matchingRecords = [];
        
        // Use a cursor to iterate through all records
        const cursorRequest = store.openCursor();
        
        cursorRequest.onsuccess = (event) => {
          const cursor = event.target.result;
          
          if (cursor) {
            const record = cursor.value;
            
            // Check if record matches the where condition
            if (this._matchesCondition(record, whereCondition)) {
              matchingRecords.push(record);
            }
            
            // Move to the next record
            cursor.continue();
          } else {
            // No more records
            console.log(`Found ${matchingRecords.length} matching entries in ${storeName}`);
            resolve(matchingRecords);
          }
        };
        
        cursorRequest.onerror = (event) => {
          console.error(`Error finding entries in ${storeName}:`, event.target.error);
          reject(event.target.error);
        };
      } catch (error) {
        console.error("Error in getEntriesWhere:", error);
        reject(error);
      }
    });
  }

  /**
   * Check if a record matches a condition
   * @param {Object} record - The record to check
   * @param {Object} condition - The condition to match
   * @returns {boolean} - True if the record matches the condition
   * 
   * Supports:
   * - Simple equality: { field: value }
   * - Complex conditions: { field: { $gt: value } }
   * - Multiple conditions: { field1: value1, field2: value2 }
   */
  _matchesCondition(record, condition) {
    // If condition is null or undefined, match all records
    if (!condition) {
      return true;
    }
    
    // Check each condition field
    for (const key in condition) {
      // Skip if the property doesn't exist on the object
      if (!Object.prototype.hasOwnProperty.call(condition, key)) {
        continue;
      }
      
      const conditionValue = condition[key];
      
      // Handle complex condition objects
      if (typeof conditionValue === 'object' && conditionValue !== null && !Array.isArray(conditionValue)) {
        // Check for operators
        for (const operator in conditionValue) {
          if (!Object.prototype.hasOwnProperty.call(conditionValue, operator)) {
            continue;
          }
          
          const operatorValue = conditionValue[operator];
          
          switch (operator) {
            case '$eq':
              if (record[key] !== operatorValue) return false;
              break;
            case '$ne':
              if (record[key] === operatorValue) return false;
              break;
            case '$gt':
              if (!(record[key] > operatorValue)) return false;
              break;
            case '$gte':
              if (!(record[key] >= operatorValue)) return false;
              break;
            case '$lt':
              if (!(record[key] < operatorValue)) return false;
              break;
            case '$lte':
              if (!(record[key] <= operatorValue)) return false;
              break;
            case '$in':
              if (!Array.isArray(operatorValue) || !operatorValue.includes(record[key])) return false;
              break;
            case '$nin':
              if (!Array.isArray(operatorValue) || operatorValue.includes(record[key])) return false;
              break;
            case '$exists':
              const hasProperty = record.hasOwnProperty(key);
              if (operatorValue && !hasProperty) return false;
              if (!operatorValue && hasProperty) return false;
              break;
            case '$regex':
              if (!(typeof record[key] === 'string' && new RegExp(operatorValue).test(record[key]))) return false;
              break;
            default:
              console.warn(`Unknown operator: ${operator}`);
              return false;
          }
        }
      } else {
        // Simple equality check
        if (record[key] !== conditionValue) {
          return false;
        }
      }
    }
    
    // If we got here, all conditions matched
    return true;
  }

  /**
   * Delete an entry from a store
   * @param {string} storeName - The name of the store
   * @param {string|number|Array} key - The key of the entry to delete
   * @returns {Promise} - Resolves when the entry is deleted
   */
  deleteEntry(storeName, key) {
    return new Promise((resolve, reject) => {
      try {
        const transaction = this.db.transaction([storeName], "readwrite");
        const store = transaction.objectStore(storeName);
        const request = store.delete(key);
        
        request.onsuccess = () => {
          console.log(`Deleted entry with key ${key} from ${storeName}`);
          resolve();
        };
        
        request.onerror = (event) => {
          console.error(`Error deleting entry from ${storeName}:`, event.target.error);
          reject(event.target.error);
        };
      } catch (error) {
        console.error("Error in deleteEntry:", error);
        reject(error);
      }
    });
  }

  // Other existing methods from your IndexedDBManager class
  // addEntry, updateEntry, getEntry, getAllEntries, clearStore, etc.
  
  /**
   * Add an entry to a store
   * @param {string} storeName - The name of the store
   * @param {Object} data - The data to add
   * @returns {Promise} - Resolves with the key of the added entry
   */
  addEntry(storeName, data) {
    return new Promise((resolve, reject) => {
      try {
        const transaction = this.db.transaction([storeName], "readwrite");
        const store = transaction.objectStore(storeName);
        const request = store.add(data);
        
        request.onsuccess = (event) => {
          console.log(`Added entry to ${storeName} with key ${event.target.result}`);
          resolve(event.target.result);
        };
        
        request.onerror = (event) => {
          console.error(`Error adding entry to ${storeName}:`, event.target.error);
          reject(event.target.error);
        };
      } catch (error) {
        console.error("Error in addEntry:", error);
        reject(error);
      }
    });
  }

  /**
   * Update an entry in a store
   * @param {string} storeName - The name of the store
   * @param {Object} data - The data to update
   * @returns {Promise} - Resolves with the key of the updated entry
   */
  updateEntry(storeName, data) {
    return new Promise((resolve, reject) => {
      try {
        const transaction = this.db.transaction([storeName], "readwrite");
        const store = transaction.objectStore(storeName);
        const request = store.put(data);
        
        request.onsuccess = (event) => {
          console.log(`Updated entry in ${storeName} with key ${event.target.result}`);
          resolve(event.target.result);
        };
        
        request.onerror = (event) => {
          console.error(`Error updating entry in ${storeName}:`, event.target.error);
          reject(event.target.error);
        };
      } catch (error) {
        console.error("Error in updateEntry:", error);
        reject(error);
      }
    });
  }

  /**
   * Get an entry from a store by its key
   * @param {string} storeName - The name of the store
   * @param {string|number|Array} key - The key of the entry to get
   * @returns {Promise} - Resolves with the entry
   */
  getEntry(storeName, key) {
    return new Promise((resolve, reject) => {
      try {
        const transaction = this.db.transaction([storeName], "readonly");
        const store = transaction.objectStore(storeName);
        const request = store.get(key);
        
        request.onsuccess = (event) => {
          resolve(event.target.result);
        };
        
        request.onerror = (event) => {
          console.error(`Error getting entry from ${storeName}:`, event.target.error);
          reject(event.target.error);
        };
      } catch (error) {
        console.error("Error in getEntry:", error);
        reject(error);
      }
    });
  }

  /**
   * Get all entries from a store
   * @param {string} storeName - The name of the store
   * @returns {Promise} - Resolves with an array of entries
   */
  getAllEntries(storeName) {
    return new Promise((resolve, reject) => {
      try {
        const transaction = this.db.transaction([storeName], "readonly");
        const store = transaction.objectStore(storeName);
        const request = store.getAll();
        
        request.onsuccess = (event) => {
          resolve(event.target.result);
        };
        
        request.onerror = (event) => {
          console.error(`Error getting all entries from ${storeName}:`, event.target.error);
          reject(event.target.error);
        };
      } catch (error) {
        console.error("Error in getAllEntries:", error);
        reject(error);
      }
    });
  }

  /**
   * Clear all entries from a store
   * @param {string} storeName - The name of the store
   * @returns {Promise} - Resolves when the store is cleared
   */
  clearStore(storeName) {
    return new Promise((resolve, reject) => {
      try {
        const transaction = this.db.transaction([storeName], "readwrite");
        const store = transaction.objectStore(storeName);
        const request = store.clear();
        
        request.onsuccess = () => {
          console.log(`Cleared all entries from ${storeName}`);
          resolve();
        };
        
        request.onerror = (event) => {
          console.error(`Error clearing entries from ${storeName}:`, event.target.error);
          reject(event.target.error);
        };
      } catch (error) {
        console.error("Error in clearStore:", error);
        reject(error);
      }
    });
  }
}

/**
 * Create an object store with its indices
 * @param {IDBDatabase} db - The database connection
 * @param {Object} tableConfig - The table configuration
 */
function createObjectStore(db, tableConfig) {
  if (!db.objectStoreNames.contains(tableConfig.name)) {
    const storeOptions = {};
    
    if (tableConfig.keyPath) {
      storeOptions.keyPath = tableConfig.keyPath;
    }
    
    if (tableConfig.autoIncrement) {
      storeOptions.autoIncrement = true;
    }
    
    const store = db.createObjectStore(tableConfig.name, storeOptions);
    
    // Create indices
    if (tableConfig.indices && Array.isArray(tableConfig.indices)) {
      tableConfig.indices.forEach(indexConfig => {
        const indexOptions = {};
        
        if (indexConfig.unique) {
          indexOptions.unique = true;
        }
        
        store.createIndex(indexConfig.name, indexConfig.keyPath, indexOptions);
      });
    }
  }
}

/**
 * Get the current version of the database
 * @returns {Promise} - Resolves with the current version or 0 if database doesn't exist
 */
function getDatabaseVersion() {
  return new Promise((resolve) => {
    // Try to open the database without specifying a version
    const request = indexedDB.open(DB_NAME);
    
    // Will be called if the database doesn't exist
    request.onupgradeneeded = function() {
      // If it doesn't exist, abort the request and return 0
      request.transaction.abort();
      resolve(0);
    };
    
    // If we successfully opened the database
    request.onsuccess = function(event) {
      const db = event.target.result;
      const version = db.version;
      
      // Close the database
      db.close();
      
      // Return the version
      console.log(`Detected existing database version: ${version}`);
      resolve(version);
    };
    
    // If there was an error opening the database
    request.onerror = function() {
      // If there was an error, assume database doesn't exist
      resolve(0);
    };
    
    // Handle blocked state (if database is open elsewhere)
    request.onblocked = function() {
      console.warn("Database access is blocked. Close other tabs/windows using the database.");
      // Try to resolve with a default version anyway
      resolve(0);
    };
  });
}

/**
 * Initialize the database connection
 * @param {number} version - The version to use
 * @returns {Promise} - Resolves with the database manager instance
 */
function initializeDatabase(version) {
  // Define your schema tables
  const TABLES = [
    {
      name: "user",
      keyPath: "user_guid",
      indices: [
        { name: "username", keyPath: "username", unique: false }
        // Add other indices as needed
      ]
    },
    {
      name: "formID",
      keyPath: "id",
      indices: [
        { name: "form_type", keyPath: "form_type", unique: false },
        { name: "form_type_leased", keyPath: ["form_type", "leased"], unique: false }
      ]
    }
    // Add other tables as needed
  ];

  return new Promise((resolve, reject) => {
    const request = indexedDB.open(DB_NAME, version);
    
    request.onupgradeneeded = (event) => {
      const db = event.target.result;
      
      // If this is a new database or we're upgrading
      if (event.oldVersion < version) {
        console.log(`Upgrading database from version ${event.oldVersion} to ${version}`);
        
        // Create all object stores as defined in schema
        TABLES.forEach(tableConfig => {
          createObjectStore(db, tableConfig);
        });
      }
    };
    
    request.onsuccess = (event) => {
      const db = event.target.result;
      
      // Create a manager instance
      const dbManager = new IndexedDBManager(db);
      
      // Store the database manager in instance.idb
      if (typeof instance === 'undefined') {
        window.instance = { idb: dbManager };
      } else {
        instance.idb = dbManager;
      }
      
      console.log(`Connected to digitalForms database version ${db.version}`);
      resolve(dbManager);
    };
    
    request.onerror = (event) => {
      console.error("Database error:", event.target.error);
      reject(event.target.error);
    };
  });
}

// Main initialization function
function initialize() {
  // First, get the current version
  return getDatabaseVersion()
    .then(version => {
      // If the database doesn't exist, use version 1
      // Otherwise, use the existing version
      const versionToUse = version === 0 ? 1 : version;
      
      // Initialize the database with the correct version
      return initializeDatabase(versionToUse);
    })
    .then(() => {
      console.log("digitalForms database initialized and ready to use");
      console.log("You can now use instance.idb.deleteEntry(), instance.idb.addEntry(), etc.");
      return instance.idb;
    })
    .catch(error => {
      console.error("Failed to initialize database:", error);
      throw error;
    });
}

// Start the initialization process
initialize()
  .then(() => {
    // You can add any initialization code here
    console.log("Ready to use instance.idb methods");
  })
  .catch(error => {
    console.error("Initialization failed:", error);
  });

// Example usage:
// instance.idb.deleteEntry("formID", "form123")
//   .then(() => {
//     console.log("Form deleted successfully");
//   })
//   .catch(error => {
//     console.error("Error deleting form:", error);
//   });

/**
 * Unleases form IDs by setting their 'leased' property to false
 * @param {Object} data - The data object containing form information
 * @param {string} type - The type of unlease operation ('UNLEASE_ALL' or 'UNLEASE_UNUSED')
 * @returns {Promise} - Resolves when all unlease operations are complete
 */
function unleaseIds(data, type) {
  // Get the IndexedDB manager from the instance
  const indexedDbManger = instance.root.getComponent("indexedDbManger");
  
  // Collect form numbers based on what's available in data
  const forms = {};
  
  // Get VI form number
  const viNumber = instance.root.getComponent('VI_number')?.getValue();
  if (viNumber) {
    forms.VI_number = viNumber.toString().slice(0, -1);
  }
  
  // Get 24-hour form number
  const twentyFourHourNumber = instance.root.getComponent('twenty_four_hour_number')?.getValue();
  if (twentyFourHourNumber) {
    forms.twenty_four_hour_number = twentyFourHourNumber;
  }
  
  // Get 12-hour form number
  const twelveHourNumber = instance.root.getComponent('twelve_hour_number')?.getValue();
  if (twelveHourNumber) {
    forms.twelve_hour_number = twelveHourNumber;
  }
  
  console.log('Potential forms to unlease:', forms);
  
  // Determine which forms to unlease based on the type parameter
  const formsToUnlease = {};
  
  if (type === 'UNLEASE_ALL') {
    // Unlease all forms
    Object.assign(formsToUnlease, forms);
  } else if (type === 'UNLEASE_UNUSED') {
    // Unlease only unused forms
    if (forms.VI_number && !data.VI) {
      formsToUnlease.VI_number = forms.VI_number;
    }
    
    if (forms.twenty_four_hour_number && !data.TwentyFourHour) {
      formsToUnlease.twenty_four_hour_number = forms.twenty_four_hour_number;
    }
    
    if (forms.twelve_hour_number && !data.TwelveHour) {
      formsToUnlease.twelve_hour_number = forms.twelve_hour_number;
    }
  }
  
  console.log('Forms to unlease:', formsToUnlease);
  
  // Create an array of promises for each unlease operation
  const unleasePromises = [];
  
  // Update data to set for all unleased forms
  const updateData = {
    leased: false,
    last_updated: new Date().toISOString()
  };
  
  // Process each form that needs to be unleased
  Object.entries(formsToUnlease).forEach(([formType, formId]) => {
    if (formId) {
      // Use updateEntryWhere to directly update the record by ID
      const unleasePromise = indexedDbManger.idb.updateEntryWhere('formID', 
        { id: formId }, 
        updateData
      )
        .then(count => {
          if (count > 0) {
            console.log(`Successfully unleased ${formType} with ID: ${formId}`);
          } else {
            console.warn(`Form with ID ${formId} not found, nothing to unlease`);
          }
        })
        .catch(error => {
          console.error(`Error unleasing ${formType} with ID ${formId}:`, error);
        });
      
      unleasePromises.push(unleasePromise);
    }
  });
  
  // Wait for all unlease operations to complete
  return Promise.all(unleasePromises)
    .then(() => {
      console.log('All form unlease operations completed');
    })
    .catch(error => {
      console.error('Error during form unleasing:', error);
    });
}

/**
 * Get form numbers from data and delete corresponding forms from IndexedDB
 * @param {Object} data - The data containing form flags
 * @returns {Promise} - Resolves when all deletion operations are complete
 */
function getFormNumbersAndDelete(data) {
  // Get the IndexedDB manager from the instance
  const indexedDbManger = instance.root.getComponent("indexedDbManger");
  
  // Collect form numbers based on what's available in data
  const forms = {};
  
  if (data.VI) {
    forms.VI_number = instance.root.getComponent('VI_number')?.getValue().toString().slice(0, -1);
  }
  
  if (data.TwentyFourHour) {
    forms.twenty_four_hour_number = instance.root.getComponent('twenty_four_hour_number')?.getValue();
  }
  
  if (data.TwelveHour) {
    forms.twelve_hour_number = instance.root.getComponent('twelve_hour_number')?.getValue();
  }
  
  console.log('Forms to delete:', forms);
  
  // Create an array of promises for each deletion operation
  const deletionPromises = [];
  
  // Delete VI form if it exists
  if (forms.VI_number) {
    const viPromise = indexedDbManger.idb.deleteEntry('formID', forms.VI_number)
      .then(() => {
        console.log(`Successfully deleted VI form with ID: ${forms.VI_number}`);
      })
      .catch(error => {
        console.error(`Error deleting VI form with ID ${forms.VI_number}:`, error);
      });
    
    deletionPromises.push(viPromise);
  }
  
  // Delete 24-hour form if it exists
  if (forms.twenty_four_hour_number) {
    const twentyFourHourPromise = indexedDbManger.idb.deleteEntry('formID', forms.twenty_four_hour_number)
      .then(() => {
        console.log(`Successfully deleted 24-hour form with ID: ${forms.twenty_four_hour_number}`);
      })
      .catch(error => {
        console.error(`Error deleting 24-hour form with ID ${forms.twenty_four_hour_number}:`, error);
      });
    
    deletionPromises.push(twentyFourHourPromise);
  }
  
  // Delete 12-hour form if it exists
  if (forms.twelve_hour_number) {
    const twelveHourPromise = indexedDbManger.idb.deleteEntry('formID', forms.twelve_hour_number)
      .then(() => {
        console.log(`Successfully deleted 12-hour form with ID: ${forms.twelve_hour_number}`);
      })
      .catch(error => {
        console.error(`Error deleting 12-hour form with ID ${forms.twelve_hour_number}:`, error);
      });
    
    deletionPromises.push(twelveHourPromise);
  }
  
  // Wait for all deletion operations to complete
  return Promise.all(deletionPromises)
    .then(() => {
      console.log('All form deletion operations completed');
    })
    .catch(error => {
