}


// Function to update records in IndexedDB
async function updateFormStatus(action) {
  try {
    // Create the current timestamp in ISO format
    const currentTimestamp = new Date().toISOString();
    
    // Get all possible form numbers from the data object
    const VI_number = data.VI_number;
    const twenty_four_hour_number = data.twenty_four_hour_number;
    const twelve_hour_number = data.twelve_hour_number;
    
    // Check if we have any form numbers to update
    if (!VI_number && !twenty_four_hour_number && !twelve_hour_number) {
      console.error('No form numbers found to update in IndexedDB');
      return false;
    }
    
    // Determine which timestamp field to update based on action
    const timestampField = action === 'markSpoiled' ? 'spoiled_timestamp' : 'printed_timestamp';
    
    // Prepare updates for each form number if it exists
    const updatePromises = [];
    const tableName = 'formID';
    
    // Update VI number if it exists
    if (VI_number) {
      const viFilter = {
        conditions: { id: VI_number },
        updates: { [timestampField]: currentTimestamp }
      };
      updatePromises.push(window.updateStaticDataTable(tableName, viFilter));
    }
    
    // Update 24-hour number if it exists
    if (twenty_four_hour_number) {
      const twentyFourFilter = {
        conditions: { id: twenty_four_hour_number },
        updates: { [timestampField]: currentTimestamp }
      };
      updatePromises.push(window.updateStaticDataTable(tableName, twentyFourFilter));
    }
    
    // Update 12-hour number if it exists
    if (twelve_hour_number) {
      const twelveFilter = {
        conditions: { id: twelve_hour_number },
        updates: { [timestampField]: currentTimestamp }
      };
      updatePromises.push(window.updateStaticDataTable(tableName, twelveFilter));
    }
    
    // Wait for all updates to complete
    const results = await Promise.all(updatePromises);
    
    // Sum up the total number of updated records
    const totalUpdated = results.reduce((total, current) => total + current, 0);
    
    console.log(`Updated ${totalUpdated} records in IndexedDB for ${action === 'markSpoiled' ? 'spoiled' : 'printed'} forms`);
    return totalUpdated > 0;
  } catch (error) {
    console.error(`Failed to update local IndexedDB for ${action}:`, error);
    return false;
  }
}

// Function to update form status via API
async function updateFormAPI(action) {
  // Get authentication token from localStorage
  const authToken = localStorage.getItem('AUTH_TOKEN');
  if (!authToken) {
    window.toast.error('Authentication token not found');
    return false;
  }
  
  try {
    // Create the current timestamp in ISO format
    const currentTimestamp = new Date().toISOString();
    
    // Get form number based on what's available in data
    const forms = {};
    
    if (data.VI) {
      forms.VI_number = instance.root.getComponent('VI_number')?.getValue();
    }
    
    if (data.TwentyFourHour) {
      forms.twenty_four_hour_number = instance.root.getComponent('twenty_four_hour_number')?.getValue();
    }
    
    if (data.TweleveHour) {
      forms.twelve_hour_number = instance.root.getComponent('twelve_hour_number')?.getValue();
    }
    
    // Determine which timestamp field to update based on action
    const timestampField = action === 'markSpoiled' ? 'spoiled_timestamp' : 'printed_timestamp';
    
    // Prepare payload for the PATCH request
    const payload = {
      "forms": forms,
      [timestampField]: currentTimestamp
    };
    
    // Make the API call
    const apiUrl = `${window._env_.REACT_APP_RSBC_API_URL}/api/v1/forms`;
    
    const response = await fetch(apiUrl, {
      method: 'PATCH',
      headers: {
        'Authorization': `Bearer ${authToken}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(payload)
    });
    
    if (!response.ok) {
      throw new Error(`API request failed with status ${response.status}`);
    }
    
    const responseData = await response.json();
    console.log(`Form ${action === 'markSpoiled' ? 'spoiled' : 'printed'} successfully:`, responseData);
    return responseData;
  } catch (error) {
    console.error(`Failed to ${action === 'markSpoiled' ? 'spoil' : 'print'} form:`, error);
    return false;
  }
}

