// Function to generate a random ID for a specific form type
function generateFormId(formType) {
  const randomNum = Math.floor(10000000 + Math.random() * 90000000); // 8-digit random number
  return randomNum.toString()
}

// Function to create a form ID record
function createFormIdRecord(formType) {
  // Set expiry to a future date (30 days from now)
  const expiryDate = new Date();
  expiryDate.setDate(expiryDate.getDate() + 30);
  
  return {
    id: generateFormId(formType),
    form_type: formType,
    leased: false,
    lease_expiry: expiryDate.toUTCString(),
    printed_timestamp: null,
    spoiled_timestamp: null,
    user_guid: null,
    last_updated: new Date().toISOString()
  };
}

