validate.custom:
const isAtLeastOneMinuteAfter = (input, data) => {
  // Only proceed if all required data is present
  if (data?.date_of_driving && data?.time_of_driving && data?.dateReleased1 && input) {
    // Create moment objects for driving time and test time
    const dateOfDriving = moment(data.date_of_driving);
    const timeOfDriving = moment(data.time_of_driving, "HHmm");
    const dateOfTest = moment(data.dateReleased1);
    const timeOfTest = moment(input, "HHmm");

    // Combine date and time for both events
    const timeOfDrivingCareOrControl = moment(dateOfDriving)
      .add(timeOfDriving.hours(), "hours")
      .add(timeOfDriving.minutes(), "minutes");

    const testTime = moment(dateOfTest)
      .add(timeOfTest.hours(), "hours")
      .add(timeOfTest.minutes(), "minutes");

    // Check if test time is after driving time
    return timeOfDrivingCareOrControl.isBefore(testTime) ? 
      true : 
      "Release time must be at least 1 minute after time of driving - care or control";
  }
  
  // If all required fields aren't present, return true (validation passes)
  return true;
};

// Combined validation that checks both conditions
const validateReleaseTime = (input, data) => {
  // First check if it's not in the future
  const isLessThanCurrent = moment(data?.dateReleased1)
    .tz('America/Vancouver')
    .set("hour", input.slice(0, 2))
    .set("minute", input.slice(3)) <= moment().tz('America/Vancouver');
  
  const notInFutureResult = (input && data?.dateReleased1 && isLessThanCurrent) ? 
    true : 
    "Time Released cannot be in the future";
  
  // If the first validation passes, check the second condition
  if (notInFutureResult === true) {
    return isAtLeastOneMinuteAfter(input, data);
  } else {
    return notInFutureResult;
  }
};

// Usage
valid = validateReleaseTime(input, data);

