// Function to create loading overlay elements
function createLoadingOverlay() {
  const overlay = document.createElement('div');
  overlay.id = 'loadingOverlay';
  overlay.style.cssText = `
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 9999;
  `;

  const container = document.createElement('div');
  container.className = 'd-flex align-items-center';
  
  const spinner = document.createElement('div');
  spinner.className = 'spinner-border text-light';
  spinner.setAttribute('role', 'status');
  
  const loadingText = document.createElement('span');
  loadingText.className = 'ms-2 text-light';
  loadingText.textContent = 'Generating Images...';
  
  container.append(spinner, loadingText);
  overlay.appendChild(container);
  
  return overlay;
}

// Function to manage loading overlay
function manageLoadingOverlay(show) {
  const existingOverlay = document.getElementById('loadingOverlay');
  
  if (show && !existingOverlay) {
    document.body.appendChild(createLoadingOverlay());
  } else if (!show && existingOverlay) {
    existingOverlay.remove();
  }
}

// Function to save image and submit form
async function saveImageAndSubmit() {
  try {
    // Show loading overlay
    manageLoadingOverlay(true);
    
    // Get component instance
    const rsbcInstance = instance.root.getComponent("rsbcImagePoliceCopy");
    if (!rsbcInstance) {
      throw new Error("RSBC Image component not found");
    }

    // Get and process images
    const base64Images = await rsbcInstance.getBase64Images('stageTwo');
    
   // Iterate through each key in base64Images and add to data object
    Object.keys(base64Images).forEach(key => {
      data[key] = base64Images[key];
    });
    
    console.log("Images saved in submission object", data);
    
    // Submit form
    const submission = await form.submit();
    console.log('Form submitted successfully:', submission);
    
  } catch (error) {
    console.error("Error in save and submit process:", error);
    throw error; // Re-throw to be handled by caller if needed
    
  } finally {
    // Always hide loading overlay
    manageLoadingOverlay(false);
  }
}

// Execute the save and submit process
saveImageAndSubmit().catch(error => {
  // Handle any uncaught errors here
  console.error("Fatal error:", error);
  // You might want to show an error message to the user here
});
