data.custom:
values = window.fetchStaticDataFromTable("agencies").then((items) => {
  return items.map(item => ({
    value: item.agency_name,
    label: item.agency_name
  }));
}).catch((error) => {
  console.error("Error fetching data:", error);
  return [];
});

values = Promise.resolve(values);

