const authToken = localStorage.getItem('AUTH_TOKEN');
              if (!authToken) {
                window.toast.error('Authentication token not found');
                return false;
              }
              
class PDFGeneration {
    constructor(apiUrl, emailUrl, statusCallback = null) {
        this.apiUrl = apiUrl;
        this.emailUrl = emailUrl;
        this.statusCallback = statusCallback;
        this.currentPdfBlob = null;
        this.currentPdfUrl = null;
    }

    /**
     * Show status message using provided callback or console
     */
    showStatus(message, type) {
        if (this.statusCallback && typeof this.statusCallback === 'function') {
            this.statusCallback(message, type);
        } else {
            console.log(`[${type.toUpperCase()}] ${message}`);
        }
    }

    /**
     * Build request data object
     */
    buildRequestData(templateName, data) {
        return {
            template: templateName,
            data: data
        };
    }

    /**
     * Render HTML and open in new tab
     * @param {string} templateName - Template identifier
     * @param {Object} data - Data to render in template
     * @returns {Promise<boolean>} - Success status
     */
    async renderHTML(templateName, data) {
        try {
            this.showStatus('Calling render API...', 'info');
            
            const requestData = this.buildRequestData(templateName, data);
            const htmlRequestData = {
                ...requestData,
                options: {
                    type: "html",
                    filename: `mv6020.html`
                }
            };
            
            const response = await fetch(this.apiUrl, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${authToken}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(htmlRequestData)
            });
            
            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            }
            
            const html = await response.text();
            
            const blob = new Blob([html], { type: 'text/html' });
            const blobUrl = URL.createObjectURL(blob);
            
            const newWindow = window.open(blobUrl, '_blank');
            
            if (newWindow) {
                this.showStatus('HTML rendered successfully and opened in new tab!', 'success');
                
                newWindow.addEventListener('load', () => {
                    URL.revokeObjectURL(blobUrl);
                });
                
                return true;
            } else {
                this.showStatus('HTML rendered but popup was blocked. Please allow popups for this site.', 'error');
                URL.revokeObjectURL(blobUrl);
                return false;
            }
            
        } catch (error) {
            console.error('Error:', error);
            this.showStatus(`Error: ${error.message}`, 'error');
            throw error;
        }
    }

    /**
     * Generate and download PDF
     * @param {string} templateName - Template identifier
     * @param {Object} data - Data to render in template
     * @param {string} fileName - Output filename (without extension)
     * @returns {Promise<boolean>} - Success status
     */
    async downloadPDF(templateName, data, fileName = 'document') {
        try {
            this.showStatus('Generating PDF...', 'info');
            
            const requestData = this.buildRequestData(templateName, data);
            
            const pdfRequestData = {
                ...requestData,
                options: {
                    type: "pdf",
                    filename: `${fileName}.pdf`
                }
            };
            
            const response = await fetch(this.apiUrl, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${authToken}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(pdfRequestData)
            });
            
            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            }
            
            const pdfBlob = await response.blob();
            
            const downloadUrl = URL.createObjectURL(pdfBlob);
            const downloadLink = document.createElement('a');
            downloadLink.href = downloadUrl;
            downloadLink.download = `${fileName}.pdf`;
            
            document.body.appendChild(downloadLink);
            downloadLink.click();
            document.body.removeChild(downloadLink);
            
            URL.revokeObjectURL(downloadUrl);
            
            this.showStatus('PDF downloaded successfully!', 'success');
            return true;
            
        } catch (error) {
            console.error('Error:', error);
            this.showStatus(`Error: ${error.message}`, 'error');
            throw error;
        }
    }

    async sendEmail(templateName, data, fileName = 'document') {
        try {
            this.showStatus('Sending email...', 'info');
            
            const requestData = this.buildRequestData(templateName, data);
            
            const emailRequestData = {
                ...requestData,
                options: {
                    type: "email",
                    filename: `${fileName}.pdf`
                }
            };
            
            const response = await fetch(this.emailUrl, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${authToken}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(emailRequestData)
            });
            
            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            }
            
            this.showStatus('Email sent successfully!', 'success');
            return true;
            
        } catch (error) {
            console.error('Error:', error);
            this.showStatus(`Error: ${error.message}`, 'error');
            throw error;
        }
    }

    /**
     * Generate PDF and return as blob
     * @param {string} templateName - Template identifier
     * @param {Object} data - Data to render in template
     * @returns {Promise<Blob>} - PDF blob
     */
    async generatePDFBlob(templateName, data) {
        try {
            this.showStatus('Generating PDF blob...', 'info');
            
            const requestData = this.buildRequestData(templateName, data);
            
            const pdfRequestData = {
                ...requestData,
                options: {
                    type: "pdf"
                }
            };
            
            
            
            const response = await fetch(this.apiUrl, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${authToken}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(pdfRequestData)
            });
            
            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            }
            
            const pdfBlob = await response.blob();
            this.showStatus('PDF blob generated successfully!', 'success');
            
            return pdfBlob;
            
        } catch (error) {
            console.error('Error:', error);
            this.showStatus(`Error: ${error.message}`, 'error');
            throw error;
        }
    }
    
    /**
 * Generate PDF and open in new tab (CSP-safe)
 * @param {string} templateName - Template identifier
 * @param {Object} data - Data to render in template
 * @param {HTMLElement} button - Preview button element (optional)
 * @returns {Promise<boolean>} - Success status
 */
async previewPDF(templateName, data, button = null) {
    if (button) {
        button.disabled = true;
        button.textContent = 'Loading Preview...';
    }
    
    try {
        this.showStatus('Generating PDF preview...', 'info');
        
        const requestData = this.buildRequestData(templateName, data);
        const pdfRequestData = {
            ...requestData,
            options: {
                "type": "pdf",
                "filename": "preview.pdf"
            }
        };
        
        const response = await fetch(this.apiUrl, {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${authToken}`,
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(pdfRequestData)
        });
        
        if (!response.ok) {
            throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }
        
        const pdfBlob = await response.blob();
        const pdfUrl = URL.createObjectURL(pdfBlob);
        
        // Clean up previous preview
        this.closePreview();
        
        // Store current preview
        this.currentPdfBlob = pdfBlob;
        this.currentPdfUrl = pdfUrl;
        
        // Open in new tab instead of iframe
        const newWindow = window.open(pdfUrl, '_blank');
        
        if (newWindow) {
            this.showStatus('PDF preview opened in new tab!', 'success');
            
            // Clean up URL when window closes or loads
            newWindow.addEventListener('beforeunload', () => {
                this.closePreview();
            });
            
            return true;
        } else {
            this.showStatus('PDF generated but popup was blocked. Please allow popups for this site.', 'error');
            this.closePreview();
            return false;
        }
        
    } catch (error) {
        console.error('Error:', error);
        this.showStatus(`Error: ${error.message}`, 'error');
        throw error;
    } finally {
        if (button) {
            button.disabled = false;
            button.textContent = 'PDF Preview';
        }
    }
}

    /**
     * Generate PDF preview and display in viewer
     * @param {string} templateName - Template identifier
     * @param {Object} data - Data to render in template
     * @param {HTMLElement} previewContainer - Container element for preview
     * @param {HTMLElement} pdfViewer - PDF viewer element (iframe/embed)
     * @param {HTMLElement} button - Preview button element (optional)
     * @returns {Promise<boolean>} - Success status
     */
    async previewPDF1(templateName, data, previewContainer, pdfViewer, button = null) {
        if (button) {
            button.disabled = true;
            button.textContent = 'Loading Preview...';
        }
        
        try {
            this.showStatus('Generating PDF preview...', 'info');
            
            const requestData = this.buildRequestData(templateName, data);
            const pdfRequestData = {
                ...requestData,
                options: {
                    "type": "pdf",
                    "filename": "preview.pdf"
                }
            };
            
            const response = await fetch(this.apiUrl, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${authToken}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(pdfRequestData)
            });
            
            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            }
            
            const pdfBlob = await response.blob();
            const pdfUrl = URL.createObjectURL(pdfBlob);
            
            // Clean up previous preview if exists
            this.closePreview();
            
            // Store current preview
            this.currentPdfBlob = pdfBlob;
            this.currentPdfUrl = pdfUrl;
            
            // Show preview
            pdfViewer.src = pdfUrl;
            previewContainer.classList.remove('hidden');
            
            this.showStatus('PDF preview loaded successfully!', 'success');
            
            // Scroll to preview
            previewContainer.scrollIntoView({ behavior: 'smooth' });
            
            return true;
            
        } catch (error) {
            console.error('Error:', error);
            this.showStatus(`Error: ${error.message}`, 'error');
            throw error;
        } finally {
            if (button) {
                button.disabled = false;
                button.textContent = 'PDF Preview';
            }
        }
    }

    /**
     * Download PDF from current preview
     * @param {string} fileName - Download filename (without extension)
     * @returns {boolean} - Success status
     */
    downloadFromPreview(fileName = 'custom_name') {
        if (!this.currentPdfBlob) {
            this.showStatus('No PDF available for download', 'error');
            return false;
        }
        
        try {
            const downloadUrl = URL.createObjectURL(this.currentPdfBlob);
            const downloadLink = document.createElement('a');
            downloadLink.href = downloadUrl;
            downloadLink.download = `${fileName}.pdf`;
            
            document.body.appendChild(downloadLink);
            downloadLink.click();
            document.body.removeChild(downloadLink);
            
            URL.revokeObjectURL(downloadUrl);
            
            this.showStatus('PDF downloaded from preview!', 'success');
            return true;
            
        } catch (error) {
            console.error('Error downloading from preview:', error);
            this.showStatus(`Download error: ${error.message}`, 'error');
            return false;
        }
    }

    /**
     * Close current PDF preview and cleanup
     * @param {HTMLElement} previewContainer - Container element for preview (optional)
     * @param {HTMLElement} pdfViewer - PDF viewer element (optional)
     */
    closePreview(previewContainer = null, pdfViewer = null) {
        // Hide preview if elements provided
        if (previewContainer) {
            previewContainer.classList.add('hidden');
        }
        
        if (pdfViewer) {
            pdfViewer.src = '';
        }
        
        // Clean up stored blob URLs
        if (this.currentPdfUrl) {
            URL.revokeObjectURL(this.currentPdfUrl);
            this.currentPdfUrl = null;
        }
        this.currentPdfBlob = null;
        
        this.showStatus('PDF preview closed', 'info');
    }

    /**
     * Check if preview is currently available
     * @returns {boolean} - True if preview is available
     */
    isPreviewAvailable() {
        return this.currentPdfBlob !== null;
    }
}

