/**
 * GPS Coordinate Conversion Utilities
 * Provides bidirectional conversion between Decimal Degrees and Degrees Minutes Seconds
 */

class GPSConverter {
  /**
   * Convert decimal degrees to degrees, minutes, seconds format
   * @param {number} decimalDegrees - The decimal degrees value
   * @returns {Object} - Object containing degrees, minutes, seconds
   */
  decimalToDMS(decimalDegrees) {
    if (decimalDegrees === null || decimalDegrees === undefined || isNaN(decimalDegrees)) {
      return { degrees: null, minutes: null, seconds: null };
    }

    const absDecimal = Math.abs(decimalDegrees);

    const degrees = Math.floor(absDecimal);
    const minutesFloat = (absDecimal - degrees) * 60;
    const minutes = Math.floor(minutesFloat);
    const seconds = (minutesFloat - minutes) * 60;

    return {
      degrees: decimalDegrees < 0 ? -degrees : degrees,
      minutes,
      seconds: Math.round(seconds * 1000000) / 1000000 // 6 decimal precision
    };
  }

  /**
   * Convert degrees, minutes, seconds to decimal degrees
   * @param {number} degrees - Degrees value (can be negative)
   * @param {number} minutes - Minutes value (0-59)
   * @param {number} seconds - Seconds value (0-59.999...)
   * @returns {number|null} - Decimal degrees value or null if invalid input
   */
  dmsToDecimal(degrees, minutes, seconds) {
    if (degrees === null || degrees === undefined || isNaN(degrees) ||
        minutes === null || minutes === undefined || isNaN(minutes) ||
        seconds === null || seconds === undefined || isNaN(seconds)) {
      return null;
    }

    const sign = degrees < 0 ? -1 : 1;
    const absDegrees = Math.abs(degrees);

    return sign * (absDegrees + (minutes / 60) + (seconds / 3600));
  }

  /**
   * Validate latitude decimal degrees
   * @param {number} lat - Latitude in decimal degrees
   * @returns {boolean} - True if valid latitude (-90 to 90)
   */
  isValidLatitude(lat) {
    return lat >= -90 && lat <= 90;
  }

  /**
   * Validate longitude decimal degrees
   * @param {number} lng - Longitude in decimal degrees
   * @returns {boolean} - True if valid longitude (-180 to 180)
   */
  isValidLongitude(lng) {
    return lng >= -180 && lng <= 180;
  }

  /**
   * Validate minutes or seconds value
   * @param {number} value - Minutes or seconds value
   * @returns {boolean} - True if valid (0 to 59.999...)
   */
  isValidMinutesSeconds(value) {
    return value >= 0 && value < 60;
  }

  /**
   * Convert latitude decimal degrees to DMS components
   * @param {number} latDecimal - Latitude in decimal degrees
   * @returns {Object} - Object with lat_degree, lat_min, lat_sec
   */
  convertLatitudeToDMS(latDecimal) {
    const dms = this.decimalToDMS(latDecimal);
    return {
      lat_degree: dms.degrees,
      lat_min: dms.minutes,
      lat_sec: dms.seconds
    };
  }

  /**
   * Convert longitude decimal degrees to DMS components
   * @param {number} lngDecimal - Longitude in decimal degrees
   * @returns {Object} - Object with long_degree, long_min, long_sec
   */
  convertLongitudeToDMS(lngDecimal) {
    const dms = this.decimalToDMS(lngDecimal);
    return {
      long_degree: dms.degrees,
      long_min: dms.minutes,
      long_sec: dms.seconds
    };
  }

  /**
   * Convert DMS components to latitude decimal degrees
   * @param {number} degrees - Latitude degrees
   * @param {number} minutes - Latitude minutes
   * @param {number} seconds - Latitude seconds
   * @returns {number|null} - Latitude in decimal degrees
   */
  convertDMSToLatitude(degrees, minutes, seconds) {
    return this.dmsToDecimal(degrees, minutes, seconds);
  }

  /**
   * Convert DMS components to longitude decimal degrees
   * @param {number} degrees - Longitude degrees
   * @param {number} minutes - Longitude minutes
   * @param {number} seconds - Longitude seconds
   * @returns {number|null} - Longitude in decimal degrees
   */
  convertDMSToLongitude(degrees, minutes, seconds) {
    return this.dmsToDecimal(degrees, minutes, seconds);
  }

}


