if(instance && !instance.mapSelectedListner){
  
  /**
 * Replaces the JavaScript file in an RSBC service URL with a GeoJSON file
 * @param {string} rsbcUrl - The original RSBC service URL
 * @param {string} geoJsonFileName - The GeoJSON file name to replace with (default: 'ABMS_PROVINCE_SP.geojson')
 * @returns {string} The modified URL with GeoJSON file
 */
function replaceWithGeoJson(rsbcUrl, geoJsonFileName = 'ABMS_PROVINCE_SP.geojson') {
  if (!rsbcUrl || typeof rsbcUrl !== 'string') {
    throw new Error('Invalid URL provided');
  }

  // Find the last occurrence of '/' to locate the file part
  const lastSlashIndex = rsbcUrl.lastIndexOf('/');
  
  if (lastSlashIndex === -1) {
    throw new Error('Invalid URL format: no path separator found');
  }

  // Extract base URL and replace the file portion
  const baseUrl = rsbcUrl.substring(0, lastSlashIndex + 1);
  
  return `${baseUrl}${geoJsonFileName}`;
}

function setGeoJsonUrl() {
  const geoUrl = replaceWithGeoJson(window._env_.REACT_APP_MF_FORMSFLOW_RSBC_SERVICE_URL);
  instance.component.geoJsonUrl = geoUrl
  instance.render()
}

