}

   instance.on('render', (event) => {
     console.log('Form rendered:', event);
    setGeoJsonUrl()
  });
  
  instance.root.on('mapSelected', async (event) => {
    const loc_data = event.data;
    setLocationInfo(loc_data)
  });
  
  instance.root.on('mapCleared', async (event) => {
    instance.root.getComponent("lat_decim_degrees").setValue('')
    instance.root.getComponent("long_decim_degrees").setValue('')
    instance.root.getComponent("address").setValue('')
  });

  instance.root.on('addressSet', async (event) => {
    const address = event.data;
    setLocationInfo(loc_data)
  });
  
};

