components.calculateValue:
/**
 * Entity Number Calculator
 * Manages automatic numbering for different entity types in edit grids
 */

class EntityNumberCalculator {
  constructor() {
    this.entityTypeConfig = {
      V: { baseNumber: 1, count: 0 },
      P: { baseNumber: 30, count: 0 },
      C: { baseNumber: 20, count: 0 }
    };
    this.defaultEntityNumber = '99';
  }

  /**
   * Reset all entity counts to zero
   */
  resetCounts() {
    Object.keys(this.entityTypeConfig).forEach(type => {
      this.entityTypeConfig[type].count = 0;
    });
  }

  /**
   * Get the next entity number for a given type
   * @param {string} entityType - The type of entity (vehicle, pedestrian, cyclist)
   * @returns {string} - Padded entity number
   */
  getNextEntityNumber(entityType) {
    const normalizedType = (entityType || '');

    if (!this.entityTypeConfig[normalizedType]) {
      return this.defaultEntityNumber;
    }

    const config = this.entityTypeConfig[normalizedType];
    const sequence = config.baseNumber + config.count;
    config.count++;

    return sequence.toString().padStart(2, '0');
  }

  /**
   * Find a component recursively within a component tree
   * @param {Array} components - Array of components to search
   * @param {string} key - Component key to find
   * @returns {Object|null} - Found component or null
   */
  findComponentRecursive(components, key) {
    for (const comp of components) {
      if (comp.component && comp.component.key === key) {
        return comp;
      }
      if (Array.isArray(comp.components)) {
        const nested = this.findComponentRecursive(comp.components, key);
        if (nested) return nested;
      }
    }
    return null;
  }

  /**
   * Recalculate entity numbers for all rows in an edit grid
   * @param {Object} editGridInstance - The edit grid component instance
   * @param {string} entityTypeKey - Key for the entity type field (default: 'entity_type')
   * @param {string} entityNumKey - Key for the entity number field (default: 'entity_num')
   */
  recalculateEntityNumbers(editGridInstance, entityTypeKey = 'entity_type', entityNumKey = 'entity_num') {
    if (!editGridInstance || !Array.isArray(editGridInstance.editRows)) {
      return;
    }

    // Reset counts for fresh calculation
    this.resetCounts();

    editGridInstance.editRows.forEach(rowInstance => {
      const entityTypeComp = this.findComponentRecursive(rowInstance.components, entityTypeKey);
      const entityNumComp = this.findComponentRecursive(rowInstance.components, entityNumKey);

      if (entityTypeComp && entityNumComp) {
        const entityType = entityTypeComp.getValue();
        const entityNumber = this.getNextEntityNumber(entityType.value);
        entityNumComp.setValue(entityNumber);
      }
    });
  }

  /**
   * Get current entity counts for debugging/reporting
   * @returns {Object} - Current counts for each entity type
   */
  getCurrentCounts() {
    const counts = {};
    Object.keys(this.entityTypeConfig).forEach(type => {
      counts[type] = this.entityTypeConfig[type].count;
    });
    return counts;
  }

  /**
   * Update entity type configuration
   * @param {string} entityType - The entity type to configure
   * @param {number} baseNumber - Base number for this entity type
   */
  updateEntityTypeConfig(entityType, baseNumber) {
    if (!this.entityTypeConfig[entityType]) {
      this.entityTypeConfig[entityType] = { count: 0 };
    }
    this.entityTypeConfig[entityType].baseNumber = baseNumber;
  }
}

