/**
 * Contributing Factor Manager
 * Handles conditional updates to contributing factor fields based on collision scenarios
 */

class ContributingFactorManager {
  constructor() {
    this.defaultValue = {
      label: "99 Other - Explain in Comments",
      value: "99"
    };
    
    // All contributing factor fields
    this.allFields = [
      'contributing_factor_1', 
      'contributing_factor_2', 
      'contributing_factor_3', 
      'contributing_factor_4'
    ];
    
    // Define scenario-based rules
    this.scenarioRules = {
      '03': {
        targetFields: this.allFields,
        value: this.defaultValue
      },
      '04': {
        targetFields: this.allFields,
        value: this.defaultValue
      },
      '05': {
        targetFields: this.allFields,
        value: this.defaultValue
      },
      '06': {
        targetFields: this.allFields,
        value: this.defaultValue
      }
    };
  }

  /**
   * Find a component recursively within a component tree
   * @param {Array} components - Array of components to search
   * @param {string} key - Component key to find
   * @returns {Object|null} - Found component or null
   */
  findComponentRecursive(components, key) {
    for (const comp of components) {
      if (comp.component && comp.component.key === key) {
        return comp;
      }
      if (Array.isArray(comp.components)) {
        const nested = this.findComponentRecursive(comp.components, key);
        if (nested) return nested;
      }
    }
    return null;
  }

  /**
   * Add or update a scenario rule
   * @param {string} scenario - Collision scenario code
   * @param {Array<string>} targetFields - Fields to update
   * @param {Object} value - Value to set
   */
  addScenarioRule(scenario, targetFields, value) {
    this.scenarioRules[scenario] = {
      targetFields,
      value
    };
  }

  /**
   * Remove a scenario rule
   * @param {string|Object} scenario - Collision scenario code or object with value property
   */
  removeScenarioRule(scenario) {
    const scenarioKey = typeof scenario === 'object' && scenario.value ? scenario.value : scenario;
    delete this.scenarioRules[scenarioKey];
  }

  /**
   * Get the rule for a specific scenario
   * @param {string|Object} scenario - Collision scenario code or object with value property
   * @returns {Object|null} - Rule object or null if not found
   */
  getScenarioRule(scenario) {
    const scenarioKey = typeof scenario === 'object' && scenario.value ? scenario.value : scenario;
    return this.scenarioRules[scenarioKey] || null;
  }

  /**
   * Enable a component and clear its value
   * @param {Object} fieldComp - The field component to reset
   */
  enableAndClearComponent(fieldComp) {
    if (!fieldComp) return;
    
    // Enable the component
    fieldComp.disabled = false;
    if (fieldComp.component) {
      fieldComp.component.disabled = false;
    }
    
    // Clear the value
    fieldComp.setValue('');
  }

  /**
   * Disable a component
   * @param {Object} fieldComp - The field component to disable
   */
  disableComponent(fieldComp) {
    if (!fieldComp) return;
    
    fieldComp.disabled = true;
    if (fieldComp.component) {
      fieldComp.component.disabled = true;
    }
  }

  /**
   * Reset all contributing factor fields - enable and clear values
   * @param {Object} editGridInstance - The edit grid component instance
   * @param {Array<string>} fieldKeys - Fields to reset (default: all fields)
   */
  resetAllContributingFactors(editGridInstance, fieldKeys = null) {
    const fields = fieldKeys || this.allFields;
    
    if (!editGridInstance || !Array.isArray(editGridInstance.editRows)) {
      return;
    }

    editGridInstance.editRows.forEach(rowInstance => {
      fields.forEach(fieldKey => {
        const fieldComp = this.findComponentRecursive(rowInstance.components, fieldKey);
        if (fieldComp) {
          this.enableAndClearComponent(fieldComp);
        }
      });
    });
  }

  /**
   * Apply contributing factor rules based on collision scenario
   * @param {Object} editGridInstance - The edit grid component instance
   * @param {string|Object} collisionScenario - The collision scenario value or object
   */
  applyScenarioRules(editGridInstance, collisionScenario) {
    if (!editGridInstance || !Array.isArray(editGridInstance.editRows)) {
      return;
    }

    // Step 1: Reset all contributing factor fields (enable and clear)
    this.resetAllContributingFactors(editGridInstance);

    // Step 2: Get rule for the current scenario
    const rule = this.getScenarioRule(collisionScenario);
    
    if (!rule) {
      // No rule defined for this scenario, all fields remain enabled and cleared
      return;
    }

    // Step 3: Apply rule - set value and disable targeted fields
    editGridInstance.editRows.forEach(rowInstance => {
      rule.targetFields.forEach(fieldKey => {
        const fieldComp = this.findComponentRecursive(rowInstance.components, fieldKey);
        
        if (fieldComp) {
          // Set the value
          fieldComp.setValue(rule.value);
          
          // Disable the component
          this.disableComponent(fieldComp);
        }
      });
    });
  }

  /**
   * Clear contributing factors for all rows (legacy method)
   * @param {Object} editGridInstance - The edit grid component instance
   * @param {Array<string>} fieldKeys - Fields to clear
   */
  clearContributingFactors(editGridInstance, fieldKeys = null) {
    this.resetAllContributingFactors(editGridInstance, fieldKeys);
  }

  /**
   * Get all configured scenario rules
   * @returns {Object} - All scenario rules
   */
  getAllRules() {
    return { ...this.scenarioRules };
  }
}

// Initialize global instance
if (typeof instance === 'undefined') {
  // window.instance = { contributingFactorManager };
} else if (typeof instance.contributingFactorManager === 'undefined') {
  const contributingFactorManager = new ContributingFactorManager();
  instance.contributingFactorManager = contributingFactorManager;
}

