class GridDataUtility {
 /**
  * Traverse up the component hierarchy to find parent grid data
  * @param {Object} instance - The component instance to start from
  * @param {string} gridType - Type of grid to search for (default: 'datagrid')
  * @returns {Object|null} - Grid data object or null if not found
  */
 getParentGridData(instance, gridType = 'editgrid') {
   let current = instance.parent;
   
   while (current) {
     if (current.component && current.component.type === gridType) {
       return current._data || null;
     }
     current = current.parent;
