// Custom validation for entity_type field in FormIO DataGrid
// Add this to the Custom Validation field of your entity_type component
const entityLimits = {
  V: 18, // Vehicle limit
  P: 9,  // Pedestrian limit
  C: 9,  // Cyclist limit
  O: 9   // Other limit
};

const editGrid = instance.root.getComponent('entities');
if (!editGrid || !Array.isArray(editGrid.editRows)) {
  valid = true;
  return;
}

// Extract the value from the entity type object
const selectedEntityType = input && typeof input === 'object' ? input.value : input;

// Skip validation for unknown entity types or empty values
if (!selectedEntityType || !entityLimits.hasOwnProperty(selectedEntityType)) {
  valid = true;
  return;
}

// Helper function to find component recursively
function findComponentRecursive(components, key) {
  for (const comp of components) {
    if (comp.component && comp.component.key === key) {
      return comp;
    }
    if (Array.isArray(comp.components)) {
      const nested = findComponentRecursive(comp.components, key);
      if (nested) return nested;
    }
  }
  return null;
}

// Count existing entities of the selected type
let entityCount = 0;
const currentRowIndex = row ? editGrid.editRows.indexOf(row) : -1;

editGrid.editRows.forEach((rowInstance, index) => {
  // Skip current row when editing existing entry
  if (index === currentRowIndex) return;
  
  const entityTypeComp = findComponentRecursive(rowInstance.components, 'entity_type');
  if (entityTypeComp) {
    const existingEntityValue = entityTypeComp.getValue();
    // Extract value from object if necessary
    const existingEntityType = existingEntityValue && typeof existingEntityValue === 'object' 
      ? existingEntityValue.value 
      : existingEntityValue;
    
    if (existingEntityType === selectedEntityType) {
      entityCount++;
    }
  }
});

// Check if adding this entity would exceed the limit
const limit = entityLimits[selectedEntityType];
if (entityCount >= limit) {
  valid = `You cannot add more entries for this entity type. You have already reached the limit of ${limit}.`;
} else {
  valid = true;
}

