  if (!instance.hasEventHandler('change')) {
    instance.on('change', (event) => {
      if (event.changed && event.changed.component.key === instance.component.key) {
        handleEventTypeChange(instance, event.changed.value.value);
        setInvolvedPersonFields(event.changed.value.value);
      }
    });
  }
}

function handleEventTypeChange(instance, selectedValue) {
  recalculateEntityNumbers(instance);
  
  const components = getRelevantComponents(instance);
  
  // Reset all fields first
  resetAllFields(components);
  
  // Apply specific configurations based on event type
  if (selectedValue === "P") {
    configurePedestrianFields(components);
  } else if (selectedValue === "C") {
    configureCyclistFields(components);
  }
  // else: fields remain reset (enabled and empty)
}

function recalculateEntityNumbers(instance) {
  const calculator = instance.root.getComponent('entityNumberCalculator');
  const entities = instance.root.getComponent('entities');
  calculator.entityNumberCalculator.recalculateEntityNumbers(entities);
}

function getRelevantComponents(instance) {
