try {
  const value = instance.getValue();
  
  // Early return if value is empty, blank, undefined, or null
  if (!value || value.trim() === '') {
    return true;
  }
  
  // Parse the date in yyyyMMdd format
  let inputDate;
  if (value.length === 8 && !value.includes('-')) {
    const year = value.substring(0, 4);
    const month = value.substring(4, 6);
    const day = value.substring(6, 8);
    inputDate = new Date(`${year}-${month}-${day}`);
  } else {
    inputDate = new Date(value);
  }
  
  // Check if date is valid
  if (isNaN(inputDate.getTime())) {
    return "Invalid Date of Birth";
  }
  
  // Use logic similar to the Yup validation
  const currentDate = new Date();
  
  // Calculate age in years
  let ageInYears = currentDate.getFullYear() - inputDate.getFullYear();
  
  // Get month and day for current date and input date
  const currentMonth = currentDate.getMonth();
  const currentDay = currentDate.getDate();
  const inputMonth = inputDate.getMonth();
  const inputDay = inputDate.getDate();
  
  // Check if birthday hasn't occurred yet this year
  if (
    inputMonth > currentMonth || 
    (inputMonth === currentMonth && inputDay > currentDay)
  ) {
    ageInYears--;
  }
  
  // Handle negative age (future dates) - display as 0
  if (ageInYears < 0) {
    ageInYears = 0;
  }
  
  // Format the label with age
  const newLabel = `Date of Birth (${ageInYears} yrs)`;
  instance.component.label = newLabel;
  instance.redraw();
  
  // Validate age range
  if (ageInYears < 10) {
    return "Driver must be at least 10 years old";
  }
  
  if (ageInYears > 120) {
    return "Driver cannot be older than 120 years";
  }
  
  return true;
} catch (error) {
  console.error("Error in date of birth logic:", error);
  return true;
}

