// Levenshtein distance implementation
function levenshteinDistance(a, b) {
  const matrix = Array(b.length + 1).fill(null)
    .map(() => Array(a.length + 1).fill(null));

  for (let i = 0; i <= a.length; i++) matrix[0][i] = i;
  for (let j = 0; j <= b.length; j++) matrix[j][0] = j;

  for (let j = 1; j <= b.length; j++) {
    for (let i = 1; i <= a.length; i++) {
      const cost = a[i - 1] === b[j - 1] ? 0 : 1;
      matrix[j][i] = Math.min(
        matrix[j][i - 1] + 1,      // deletion
        matrix[j - 1][i] + 1,      // insertion
        matrix[j - 1][i - 1] + cost // substitution
      );
    }
  }

  return matrix[b.length][a.length];
}

// Find best match with threshold
function findBestMatch(target, items, threshold = 2) {
  let bestMatch = null;
  let bestDistance = Infinity;

  for (const item of items) {
    const itemValue = item.label || item.value;
    const distance = levenshteinDistance(target.toLowerCase(), itemValue.toLowerCase());

    if (distance < bestDistance && distance <= threshold) {
      bestDistance = distance;
      bestMatch = item;
    }
  }

  return bestMatch;
}

(async function() {
  // Define isPromise function
  const isPromise = (obj) => {
    return !!obj && (typeof obj === 'object' || typeof obj === 'function') && typeof obj.then === 'function';
  };

  const vehicle_plate_no = row.vehicle_plate_num;
  if (!vehicle_plate_no) {
    window.toast.error("Enter Vehicle plate number");
    return;
  }

  const authToken = localStorage.getItem('AUTH_TOKEN');
  if (!authToken) {
    window.toast.error('Authentication token not found');
    return;
  }

  try {
    // Get the API URL from environment variables and format it with the vehicle plate number
    const apiUrl = `${window._env_.REACT_APP_RSBC_API_URL}/api/v1/icbc/vehicles/${vehicle_plate_no}`;

    const response = await fetch(apiUrl, {
      headers: {
        'Authorization': `Bearer ${authToken}`,
        'Content-Type': 'application/json'
      }
    });

    if (!response.ok) {
      throw new Error('API request failed');
    }

    const resp = await response.json();

    if (Array.isArray(resp) && resp.length > 0) {
      console.log("Found Vehicle!", resp);
      const vehicle = resp[0];
      const party = vehicle.vehicleParties[0].party;
      const address = party.addresses[0];

      // Find the editGrid component
      let editGridComponent = instance;
      while (editGridComponent && editGridComponent.component.type !== 'editgrid') {
        editGridComponent = editGridComponent.parent;
      }

      // Get the current row index from instance
      const rowIndex = instance.rowIndex;

      // Helper function to get component for current row
      const getRowComponent = (componentKey) => {
        if (!editGridComponent) return null;
        const components = editGridComponent.getComponent(componentKey);
        if (Array.isArray(components) && rowIndex >= 0 && rowIndex < components.length) {
          return components[rowIndex];
        }
        return components;
      };

      // Update row object directly
      row.nsc_num = vehicle.nscNumber;

      // For color handling
      const colourComponent = getRowComponent('vehicle_colour');
      if (colourComponent && typeof colourComponent.getCustomItems === 'function') {
        const colourItems = colourComponent.getCustomItems();

        const processColour = (items) => {
          const matchingColour = items.find(item => 
            item.label === vehicle.vehicleColour.toUpperCase() || 
            item.value === vehicle.vehicleColour
          );
          if (matchingColour) {
            // row.vehicle_colour = matchingColour;
             colourComponent.setValue(matchingColour.value)
             colourComponent.redraw()
          }
        };

        if (isPromise(colourItems)) {
          await colourItems.then(processColour);
        } else if (colourItems) {
          processColour(colourItems);
        }
      }

      // For vehicle make/model
      const mkMdComponent = getRowComponent('vehicle_make');
      if (mkMdComponent && typeof mkMdComponent.getCustomItems === 'function') {
        const mkMdValue = vehicle.vehicleMake + " - " + vehicle.vehicleModel;
        const mkMdItems = mkMdComponent.getCustomItems();

        const processItems = (items) => {
          // First try exact match
          let match = items.find(item => 
            item.label === mkMdValue || 
            item.value === mkMdValue
          );

          // If no exact match, try fuzzy matching
          if (!match && typeof findBestMatch === 'function') {
            match = findBestMatch(mkMdValue, items);
          }

          // If matched set the value
          if (match) {
            row.vehicle_make = match;
          }
        };

        if (isPromise(mkMdItems)) {
          await mkMdItems.then(processItems);
        } else if (mkMdItems) {
          processItems(mkMdItems);
        }
      }

      // For year handling
      const yearComponent = getRowComponent('vehicle_year');
      if (yearComponent && typeof yearComponent.getCustomItems === 'function') {
        const yearItems = yearComponent.getCustomItems();

        const processYear = (items) => {
          const matchingYear = items.find(item => 
            item.label === vehicle.vehicleModelYear || 
            item.value === vehicle.vehicleModelYear
          );
          row.vehicle_year = matchingYear || {
            value: vehicle.vehicleModelYear,
            label: vehicle.vehicleModelYear
          };
        };

        if (isPromise(yearItems)) {
          await yearItems.then(processYear);
        } else if (yearItems) {
          processYear(yearItems);
        }
      }

      // Set jurisdiction
      row.vehicle_plate_prov_state = {
        value: "CA_BC",
        label: "BRITISH COLUMBIA",
      };

      // Set owner information
      row.vehicle_owner_name = party.firstName + ' ' + party.lastName;
      row.vehicle_owner_address = address.addressLine1;

      // Trigger editGrid redraw
      if (editGridComponent) {
        editGridComponent.redraw();
        editGridComponent.triggerChange();
      }

      window.toast.success('Vehicle details loaded successfully');
    } else {
      console.error("No vehicle was found using this plate number", resp);
      window.toast.error("No vehicle was found using this plate number.");
    }
  } catch (error) {
    console.error('Failed to fetch vehicle details:', error);
    window.toast.error("No vehicle was found using this plate number.");
  }
})();

