components.customConditional:
// On change 
if (instance) {
  if (!instance.hasEventHandler('change')) {
    instance.on('change', (event) => {
      if (event.changed && event.changed.component.key === instance.component.key) {
        handleEventTypeChange(instance, event.changed.value);
      }
    });
  }
}

function handleEventTypeChange(instance, selectedValue) {
  
  const components = getRelevantComponents(instance);
  
  // Reset all fields first
  resetAllFields(components);
  
  // Apply specific configurations based on event type
  if (selectedValue === "notInjured") {
    configureNotInjured(components);
  } else if (selectedValue === "deceased") {
    configureDeceased(components);
  }
  // else: fields remain reset (enabled and empty)
}

function getRelevantComponents(instance) {
  return {
    severe_injury_location: instance.root.getComponent("severe_injury_location"),
    injury_type: instance.root.getComponent("injury_type"),
    injury_classification: instance.root.getComponent("injury_classification"),
    consciousness_state: instance.root.getComponent("consciousness_state"),
  };
}

function resetAllFields(components) {
  Object.values(components).forEach(component => {
    component.setValue('');
    component.component.disabled = false;
    component.disabled = false;
  });
}

function configureNotInjured(components) {
  setValueAndDisable(components.severe_injury_location, {
        "label": "01 No Injury",
        "value": "01"
      });
  setValueAndDisable(components.injury_type, {
        "label": "01 None",
        "value": "01"
      });
  setValueAndDisable(components.consciousness_state, {
        "label": "01 Apparently Normal",
        "value": "01"
      });
  setValueAndDisable(components.injury_classification, {
        "label": "01 No Apparent Injuries",
        "value": "01"
      });
}

function configureDeceased(components) {
  setValueAndDisable(components.consciousness_state, {
        "label": "06 Apprarently Dead",
        "value": "06"
      });
  setValueAndDisable(components.injury_classification, {
        "label": "98 Apparently deceased or deceased",
        "value": "98"
      });
}

function setValueAndDisable(component, value) {
  component.setValue(value);
  component.component.disabled = true;
  component.disabled = true;}

