components.customConditional:
if(!instance.readyEvent){
  // Also try on form ready
  instance.root.ready.then(() => {
    const interactiveKeys = ['printFiledSet'];
    
    // Function to recursively enable all components
    function enableComponentRecursively(component) {
      if (!component) return;
      
      // Enable the current component
      component.disabled = false;
      component.readonly = false;
      if (component.options) {
        component.options.readOnly = false;
      }
      
      // Force enable DOM elements
      if (component.element) {
        const elements = component.element.querySelectorAll('input, button, select, textarea');
        elements.forEach(el => {
          el.disabled = false;
          el.readOnly = false;
          el.style.pointerEvents = 'auto';
          el.style.opacity = '1';
        });
      }
      
      // For button components specifically
      if (component.type === 'button' && component.refs?.button) {
        component.refs.button.disabled = false;
        component.refs.button.style.pointerEvents = 'auto';
      }
      
      // Recursively enable child components
      if (component.components && Array.isArray(component.components)) {
        component.components.forEach(childComponent => {
          enableComponentRecursively(childComponent);
        });
      }
      
      // Handle columns component children
      if (component.columns && Array.isArray(component.columns)) {
        component.columns.forEach(column => {
          if (column.components && Array.isArray(column.components)) {
            column.components.forEach(childComponent => {
              enableComponentRecursively(childComponent);
            });
          }
        });
      }
      
      // Handle nested components in other container types
      if (component._components && Array.isArray(component._components)) {
        component._components.forEach(childComponent => {
          enableComponentRecursively(childComponent);
        });
      }
      
      // Redraw the component
      if (component.redraw) {
        component.redraw();
      }
    }
    
    interactiveKeys.forEach(key => {
      const component = instance.root.getComponent(key);
      if (component) {
        enableComponentRecursively(component);
      }
    });
  });
  instance.readyEvent = true;
}

