function calcualtePedestrianLogic() {
  const pedestrian_action = instance.root.getComponent("pedestrian_action")
  const pedestrian_location = instance.root.getComponent("pedestrian_location")
  const hasEntityType = (entities, targetType) =>
    entities.some(entity => entity.entity_type.value === targetType);
  const isPedestrianExists = hasEntityType(data.entities, "P");
  if (!isPedestrianExists) {

    pedestrian_action.setValue({
        "label": "98 No Pedestrian Involved",
        "value": "98"
      })
    pedestrian_location.setValue({
        "label": "98 No Pedestrian Involved",
        "value": "98"
      })
    pedestrian_action.component.disabled = true
    pedestrian_location.component.disabled = true
    pedestrian_action.redraw();
    pedestrian_location.redraw();

  } else {
    pedestrian_action.setValue("")
    pedestrian_location.setValue("")
    pedestrian_action.component.disabled = false
    pedestrian_location.component.disabled = false
    pedestrian_action.redraw();
    pedestrian_location.redraw();
  }
}


// tab validation logic

const tab = form.getComponent('tabs');
const currentTabIndex = tab.currentTab;

// Get all components in the current tab
const currentTabComponents = tab.tabs[currentTabIndex];

// Validate current tab components
const validationPromises = [];
const invalidComponents = [];

// Recursively validate all components in current tab
function validateTabComponents(components) {
    components.forEach(comp => {
        const componentInstance = form.getComponent(comp.key);
        if (componentInstance) {
            // Check if component is required and has validation
            if (componentInstance.component.validate && componentInstance.component.validate.required) {
                      componentInstance.setPristine(false);
                const validation = componentInstance.checkValidity();
                if (!validation) {
                    invalidComponents.push({
                        key: comp.key,
                        label: comp.label || comp.key,
                        component: componentInstance
                    });
                }
            }
            
            // Handle nested components (panels, fieldsets, etc.)
            if (comp.components && comp.components.length > 0) {
                validateTabComponents(comp.components);
            }
        }
    });
}



validateTabComponents(currentTabComponents);
if(tab.currentTab === 1 ) { // if click Next after Entity Tab
  calcualtePedestrianLogic();
}


// Proceed only if validation passes
if (invalidComponents.length === 0) {
    const newIndex = tab.currentTab + 1;
    if (newIndex < tab.tabs.length) {
        tab.setTab(newIndex);
        window.scrollTo(0, 0);
    }
} else {
    // Handle validation errors
    console.log('Validation failed for:', invalidComponents);
    
    // Optional: Focus first invalid component
    if (invalidComponents[0] && invalidComponents[0].component.focus) {
        invalidComponents[0].component.focus();
    }
    
     const errorsContainer = document.getElementById('errorsContainer');
     errorsContainer.style.display = 'block';
     // Scroll to the errors container
      errorsContainer.scrollIntoView({
        behavior: 'smooth',
        block: 'start'
      });
}

