(async function() {
  const dlNumber = data.driver_licence_no;
  if (!dlNumber) {
    console.log("Enter drivers lic number");
    return;
  }
  
  const authToken = localStorage.getItem('AUTH_TOKEN');
  if (!authToken) {
    instance.setAlert('danger', 'Authentication token not found');
    return;
  }
  
  try {
    // Get the API URL from environment variables and format it with the driver license number
    const apiUrl = `${window._env_.REACT_APP_RSBC_API_URL}/api/v1/icbc/drivers/${dlNumber}`;
    
    const response = await fetch(apiUrl, {
      headers: {
        'Authorization': `Bearer ${authToken}`,
        'Content-Type': 'application/json'
      }
    });
    
    if (!response.ok) {
      throw new Error('API request failed');
    }
    
    const resp = await response.json();
    
    if (!_.isEmpty(resp)) {
      console.log("Found driver!", resp);
      const party = resp.party;
      const address = party.addresses[0];
      
      // Using setFieldValue pattern as shown in the React code
      instance.root.getComponent('driver_last_name')?.setValue(party.lastName);
      instance.root.getComponent('driver_given_name')?.setValue(party.firstName);
      
      // Handle date format correctly using moment
      if (party.birthDate) {
        const birthDate = moment(party.birthDate).tz("America/Vancouver").toDate();
        instance.root.getComponent('driver_dob')?.setValue(birthDate);
      }
      
      instance.root.getComponent('driver_address')?.setValue(address.addressLine1);
      instance.root.getComponent('driver_city')?.setValue(address.city);
      instance.root.getComponent('driver_postal')?.setValue(address.postalCode);
      
      console.log('Driver details loaded successfully');
    } else {
      console.error("No driver was found for DL" + dlNumber, resp);
      // Display toast message for user feedback
      if (instance.setAlert) {
        instance.setAlert('danger', 'No driver was found using this DL number.');
      }
    }
  } catch (error) {
    console.log('Failed to fetch driver details. Please try again.', error);
    if (instance.setAlert) {
      instance.setAlert('danger', 'Failed to fetch driver details. Please try again.');
    }
  }
})();

