// Function to create loading overlay elements
function createLoadingOverlay() {
  const overlay = document.createElement('div');
  overlay.id = 'loadingOverlay';
  overlay.style.cssText = `
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 9999;
  `;

  const container = document.createElement('div');
  container.className = 'd-flex align-items-center';
  
  const spinner = document.createElement('div');
  spinner.className = 'spinner-border text-light';
  spinner.setAttribute('role', 'status');
  
  const loadingText = document.createElement('span');
  loadingText.className = 'ms-2 text-light';
  loadingText.textContent = 'Generating Images...';
  
  container.append(spinner, loadingText);
  overlay.appendChild(container);
  
  return overlay;
}

// Function to manage loading overlay
function manageLoadingOverlay(show) {
  const existingOverlay = document.getElementById('loadingOverlay');
  
  if (show && !existingOverlay) {
    document.body.appendChild(createLoadingOverlay());
  } else if (!show && existingOverlay) {
    existingOverlay.remove();
  }
}

